export const initialState = {
  cartProducts: [],
};
export const cartReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case "ADD_TO_CART":
      console.log(payload, "00000000000000000");
      return { cartProducts: [...state.cartProducts, payload] };
    default:
      return state;
  }
};
